import React, { useState } from 'react';
import style from './Website.module.css';

const Website = (postData) => {
  const [like, setLike] = useState(postData.amountLike);
  const changeLike = () => setLike(like + 1);

  const [post, setPost] = useState(postData.amountPost);
  const changePost = () => setPost(post + 1);

  const [repost, setRepost] = useState(postData.amountReposts);
  const changeRepost = () => {
    setRepost(repost + 1);
  };

  const [img, setImg] = useState(false);
  const changeImg = (event) => {
    setImg(event);
  };
  const [hiddenLike, setHiddenLike] = useState(true);
  const changeHiddenLike = (hidden) => {
    setHiddenLike(hidden);
  };

  return (
    <div className={style.main}>
      <div className={style.user_info}>
        <img className={style.user_image} src={postData.userImage} alt="user image" />
        <div className={style.user} onClick={() => changeImg(true)}>
          {postData.user}
        </div>
        <img className={style.marks} src={postData.marks} alt="chekmarks" />
      </div>
      <div className={style.content}>
        <div className={style.description}>{postData.description}</div>
        {img && (
          <img
            onClick={() => changeImg(false)}
            className={style.heart}
            src={postData.heart}
            alt="heart"
          />
        )}
      </div>
      <div className={style.group_like}>
        {hiddenLike && (
          <button onClick={() => changeHiddenLike(false)} className={style.pointer}>
            Like {like}
          </button>
        )}
        <button className={style.pointer} onClick={() => changeHiddenLike(true)}>
          Post {post}
        </button>
        <button className={style.pointer} onClick={changeRepost}>
          Repost {repost}
        </button>
      </div>
    </div>
  );
};

export default Website;
