import React, { useState } from 'react';
import { useSelector } from 'react-redux';
import styles from './Publication.module.css';

const Publication = ({ props }) => {
  const userLike = useSelector((store) => store.usersPosts.like);

  const [like, setLike] = useState(userLike);
  return (
    <div className={styles.main}>
      <img className={styles.image} src={props.userPhoto} alt="my image" />
      <input className={styles.box} placeholder={props.boxDescription} />
      <div className={styles.like} onClick={() => setLike(like + 1)}>
        like: {like}
      </div>
      <button onClick={() => setLike(like + 1)}>plus</button>
      <button onContextMenu={() => setLike(like - 1)}>minus</button>
    </div>
  );
};

export default Publication;
