import React, { useState } from 'react';
import styles from './Publication.module.css';

const Publication = (props) => {
  const number = 0;
  const [like, setLike] = useState(number);
  return (
    <div className={styles.main}>
      <img className={styles.image} src={post.userPhoto} alt="my image" />
      <input className={styles.box} placeholder="write something" />
      <div className={styles.like} onClick={() => setLike(like + 1)}>
        like: {like}
      </div>
      <button onClick={() => setLike(like + 1)}>plus</button>
      <button onContextMenu={() => setLike(like - 1)}>minus</button>
    </div>
  );
};

export default Publication;
